try {
                                        # MS SQL Server and Sybase with PDO_DBLIB
                                        $DBH = new PDO("mssql:host=$host;dbname=$dbname, $user, $pass");
                                        $DBH = new PDO("sybase:host=$host;dbname=$dbname, $user, $pass");

                                        # MySQL with PDO_MYSQL
                                        $DBH = new PDO("mysql:host=$host;dbname=$dbname", $user, $pass);

                                        # SQLite Database
                                        $DBH = new PDO("sqlite:my/database/path/database.db");
                                        }
                                        catch(PDOException $e) {
                                        echo $e->getMessage();
                                        }

$DBH->setAttribute( PDO::ATTR_ERRMODE,
                                        PDO::ERRMODE_SILENT );
                                        $DBH->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_WARNING );
                                        $DBH->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );

# connect to the database
                                        try {
                                        $DBH = new PDO("mysql:host=$host;dbname=$dbname", $user, $pass);
                                        $DBH->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );

                                        # UH-OH! Typed DELECT instead of SELECT!
                                        $DBH->prepare('DELECT name FROM people');
                                        }
                                        catch(PDOException $e) {
                                        echo "I'm sorry, Dave. I'm afraid I can't do that.";
                                        file_put_contents('PDOErrors.txt', $e->getMessage(), FILE_APPEND);
                                        }

# STH means "Statement Handle"
                                        $STH = $DBH->prepare("INSERT INTO folks ( first_name ) values ( 'Cathy' )");
                                        $STH->execute();

# no placeholders - ripe for SQL Injection!
                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) values ($name, $addr,
                                        $city)");

                                        # unnamed placeholders
                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) values (?, ?, ?);

                                        # named placeholders
                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) value (:name, :addr,
                                        :city)");

# assign variables to each place holder, indexed 1-3
                                        $STH->bindParam(1, $name);
                                        $STH->bindParam(2, $addr);
                                        $STH->bindParam(3, $city);

                                        # insert one row
                                        $name = "Daniel"
                                        $addr = "1 Wicked Way";
                                        $city = "Arlington Heights";
                                        $STH->execute();

                                        # insert another row with different values
                                        $name = "Steve"
                                        $addr = "5 Circle Drive";
                                        $city = "Schaumburg";
                                        $STH->execute();

# the data we want to insert
                                        $data = array('Cathy', '9 Dark and Twisty Road', 'Cardiff');

                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) values (?, ?, ?);
                                        $STH->execute($data);

# the data we want to insert
                                        $data = array( 'name' => 'Cathy', 'addr' => '9 Dark and Twisty', 'city' =>
                                        'Cardiff' );

                                        # the shortcut!
                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) value (:name, :addr,
                                        :city)");
                                        $STH->execute($data);

# a simple object
                                        class person {
                                        public $name;
                                        public $addr;
                                        public $city;

                                        function __construct($n,$a,$c) {
                                        $this->name = $n;
                                        $this->addr = $a;
                                        $this->city = $c;
                                        }
                                        # etc ...
                                        }

                                        $cathy = new person('Cathy','9 Dark and Twisty','Cardiff');

                                        # here's the fun part:
                                        $STH = $DBH->("INSERT INTO folks (name, addr, city) value (:name, :addr,
                                        :city)");
                                        $STH->execute((array)$cathy);

# using the shortcut ->query() method here since there
                                        are no variable
                                        # values in the select statement.
                                        $STH = $DBH->query('SELECT name, addr, city from folks');

                                        # setting the fetch mode
                                        $STH->setFetchMode(PDO::FETCH_ASSOC);

                                        while($row = $STH->fetch()) {
                                        echo $row['name'] . "\n";
                                        echo $row['addr'] . "\n";
                                        echo $row['city'] . "\n";
                                        }

# creating the statement
                                        $STH = $DBH->query('SELECT name, addr, city from folks');

                                        # setting the fetch mode
                                        $STH->setFetchMode(PDO::FETCH_OBJ);

                                        # showing the results
                                        while($row = $STH->fetch()) {
                                        echo $row->name . "\n";
                                        echo $row->addr . "\n";
                                        echo $row->city . "\n";
                                        }

class secret_person {
                                        public $name;
                                        public $addr;
                                        public $city;
                                        public $other_data;

                                        function __construct($other = '') {
                                        $this->address = preg_replace('/[a-z]/', 'x', $this->address);
                                        $this->other_data = $other;
                                        }
                                        }

$STH = $DBH->query('SELECT name, addr, city from folks');
                                        $STH->setFetchMode(PDO::FETCH_CLASS, 'secret_person');

                                        while($obj = $STH->fetch()) {
                                        echo $obj->addr;
                                        }

$i = 0;
                                        while($rowObj = $STH->fetch(PDO::FETCH_CLASS, 'secret_person', array($i))) {
                                        // do stuff
                                        $i++
                                        }

$sql = "SELECT COUNT(*) FROM folks";
